// REQUIRES
const { buffer, BufferType } = require('../utilities');


// CONSTRUCTOR
module.exports = function(buf) {
  const records = [];
  const variables = [];

  const ncases = buf.readUInt8(80);
  const bias = buf.readDoubleLE(84);

  const seeker = buffer.write([
    { type: BufferType.Int32, value: 7 },
    { type: BufferType.Int32, value: 4 },
    { type: BufferType.Int32, value: 8 },
    { type: BufferType.Int32, value: 3 },
  ]);

  const index = buf.indexOf(seeker);

  return { records, variables };
};